mapper.update:

UPDATE rboard 
set HIT = NVL(HIT, 0) + 1
WHERE rboard.no = #{bno}

mapper.insert:

INSERT INTO rboard VALUES (seq_rboard_no.nextval
		, #{title}
		, #{content} 
		, 0
		, sysdate
		, seq_rboard_group_no.nextval
		, 1
		, 0
		, #{uno}
		)

mapper.delete:

DELETE FROM rboard
WHERE	user_no = #{uno} 
and		no = #{bno}

mapper.update:

UPDATE rboard
SET	title = #{title}
	,content = #{content}
WHERE no = #{bno}
and user_no = #{uno}
